import { useEffect } from 'react';
import {doCreateUserWithEmailAndPassword} from '../firebase/auth';
import { useState } from 'react';

const RegisterForm = () => {

  const [password,setPassword]=useState("");
  const [activeLbl,setActiveLbl]=useState("");
  var repPassword ="";
  var email="";
  const changeLabelFocus=()=>{
    setActiveLbl(document.activeElement.className);
  }
  const passLabelHandler = ()=>{
    if(password.length<6)
      setActiveLbl("password");
    else{
      setActiveLbl("");
    }
    console.log(activeLbl)
  }
  const handleSignUpClick = ()=>{
    if(password===repPassword){
      doCreateUserWithEmailAndPassword(email,password);
    }
  };


  return (<form className="register">
    <input type="text" className="email" placeholder="email adress" onFocus={changeLabelFocus} onChange={event=>{email=event.target.value}}/>
    <input type="password" className="password" placeholder="password" onFocus={changeLabelFocus} onChange={event=>{setPassword(event.target.value); passLabelHandler();}}/>
    {activeLbl==="password"?<label className='password'>{"password must have at least 6 characters"}</label>:""}
    <input type="password" className="repPassword" onFocus={changeLabelFocus} placeholder="repeate password" onChange={event=>{repPassword=event.target.value}}/>
    {activeLbl==="repPassword"&&password!==repPassword?<label className='password'>{"passwords must be the same"}</label>:""}
    <label className='repPassword'></label>
    <div type='submit' onClick={handleSignUpClick}>Sign up</div>
  </form> );
}
 
export default RegisterForm;
