import { useState,useEffect } from "react";


function Categories(props){
  const [listVisibility, setListVisibility] = useState(false);
  

  const changeToVisible= ()=>{ 
    
    if(!listVisibility) {
      setListVisibility(true);
      
    } 
    
  }
  const changeToHidden=()=>{
    
    if(listVisibility) {
      setListVisibility(false);
    } 
  }
  var [response,setResponse] = useState();

  useEffect(()=>{
    const options = {    method: "GET", 
    headers: {
      'Access-Control-Allow-Origin':"http://localhost:3000",
      "Content-Type": "application/json"
    }};
  
    fetch('http://localhost:8080/categories',options).then(async(data)=>{
      
      return await data.json();
  
    }).then(data=>{
      
      setResponse(data);
      
      
    }).catch((err)=>{console.log(err);});

  },[])

  var len = response===undefined?0:response.len;
  var lenArr = [...Array(len).keys()];

  const [list,setList] = useState(new Array(0));

  useEffect(()=>{
    setList(new Array(0));
    lenArr.forEach((id)=>{
      const elem = response[id]
      const list_ = list;
      list_.push(elem);
      setList(list_);
      console.log(list);
    })
  },[response])
  console.log(list);

  return(
    <div className="Products" onMouseOver={changeToVisible} onMouseLeave={changeToHidden}>
      <label >Products</label>
      
      <div className={'List ' + (!listVisibility?' hide':'')} onMouseOver={changeToVisible} onMouseLeave={changeToHidden}>
      {list.map(elem=>{
        return (<div>
          {elem}
        </div>);
      })}
        
      </div>
    
    </div>
  );
}


export default Categories;
