import { useParams } from "react-router-dom";
import RegisterForm from "./Register";
import LoginForm from "./Login";
import { auth } from "../firebase/firebase";
import { useEffect, useState} from 'react';



const Signing = () => {
  const type = useParams().type==='up'?true:false;
  const [isLogged,setIsLogged] = useState(auth.currentUser?true:false);
  const [doReload,setDoReload] = useState(false);
  useEffect(()=>{ 
    setDoReload(false)
    setIsLogged(auth.currentUser?true:false);
  },[auth.currentUser,doReload])
  console.log(isLogged);
  return <div className="signingComp">{!isLogged?(type?<RegisterForm reload={setDoReload}/>:<LoginForm reload={setDoReload}/>):<div>You are already logged</div>}</div>;
}
 
export default Signing;
