import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';



function ProductPage(){

  
  const {id} = useParams();

  var [response,setResponse] = useState();

  useEffect(()=>{
    const options = {    method: "GET", 
    headers: {
      'Access-Control-Allow-Origin':"http://localhost:3000",
      "Content-Type": "application/json"
    }};
  
    fetch(`http://localhost:8080/product:${id}`,options).then((data)=>{
      
      return data.json();
  
    }).then(data=>{
      
      setResponse(data);
      
      
    }).catch((err)=>{console.log(err);});

  },[])
  console.log(response?response:{});
  const {details,main} = response?response:{};
  const {img,name,price} = main?main:{};
  const {description, available} = details?details:{};


  return(<div className="ProductPage">
    {<div className="ProductPage_imgs"><img src={response!==undefined?img:''}></img></div>}
    <div className='ProductPage_name'>name {name}</div>

    {<div className='ProductPage_price'>price {price}</div>}
    {<div className='ProductPage_available'>available {available}</div>}
    {<div className='ProductPage_description'>description {description}</div>}
  </div>)
}


export default ProductPage;
