function Searchbar(){
  return(
  <div className="Menu_Searchbar">
    <input type="text" onKeyDown={(event)=>PressedKey(event)}></input>
  </div>
  );
  function PressedKey(event){
    if(event.key=== 'Enter'){
      window.location.pathname = `/products?srtT=&cltT=&srch=${event.target.value}`;
      event.target.value="";
    }
  }
}

export default Searchbar;
