var auth;
const options = {    method: "GET", 
  headers: {
    'Access-Control-Allow-Origin':"http://localhost:3000",
    "Content-Type": "application/json"
  }};
  fetch('http://localhost:8080/auth',options).then((res)=>{
    return res.json();
  }).then(data=>{
    console.log(data);
    auth=data;
    auth.useDeviceLanguage();
  })
export{auth};
