    headers: {
      'Access-Control-Allow-Origin':`http://localhost:3000/product/${id}`,
      "Content-Type": "application/json"
    },
    body:{
      id:id
    }
  };
  
    fetch(`http://localhost:8080/product`,options).then((data)=>{
      
      return data.json();
  
