import {doCreateUserWithEmailAndPassword} from '../firebase/auth';
import { useState } from 'react';

const RegisterForm = () => {
  const inputPass = <input type="password" className="password" placeholder="password" onChange={event=>{password=event.target.value; checkPassword();}}/>;
  var password="";
  const [isValidPassword,setIsValidPassword]=useState(true);
  var repPassword ="";
  var email="";
  const checkPassword = ()=>{
    if(password.length<6)
      setIsValidPassword(false);
    else{
      setIsValidPassword(true);
    }
    console.log(isValidPassword)
  }
  const handleSignUpClick = ()=>{
    if(password===repPassword){
      doCreateUserWithEmailAndPassword(email,password);
    }
  };


  return (<form className="register">
    <input type="text" className="email" placeholder="email adress" onChange={event=>{email=event.target.value}}/>
    {inputPass}
    {inputPass.is(':focus')?<label className='password'>{!isValidPassword?"password must have at least 6 characters":""}</label>:<></>}
    <input type="password" className="repPassword" placeholder="repeate password" onChange={event=>{repPassword=event.target.value}}/>
    <label className='repPassword'></label>
    <div type='submit' onClick={handleSignUpClick}>Sign up</div>
  </form> );
}
 
export default RegisterForm;
