import { useEffect, useState } from "react";

export function BuyUI(params){

  const {available,price,name} = params;
  const [quantities,setQuantities] = useState(1);
  function changeQuantity(direction=0){
    var iqua = parseInt(quantities);


    var quantToSet=iqua+ direction<1?quantities:iqua+direction;
    
    if(quantToSet>available)quantToSet=quantities;
    setQuantities(quantToSet)
  }
  function preventInput(evt){
    ["e","E","+","-",",","."].includes(evt.key) && evt.preventDefault()
  }


  return(
  <div className="BuyUI">
    <div className="container">
      <div className="name">{name}</div>
      <div className="available">AVAILABLE: {available}</div>
      <div className="quantity">
        <button className="minus" onClick={()=>{changeQuantity(-1)}}> - </button>
        <input onKeyDown={(evt)=>preventInput(evt)} value={quantities} onChange={(e)=>{changeQuantity(e)}} type="number" min={1}/>
        <button className="plus" onClick={()=>{changeQuantity(1)}}> + </button>
      </div>
      <div className="price">
        <div className="perU">PER UNIT: ${price}</div>
        <div className="summary">{`SUMMARY: ${quantities} x $${price}`}</div>
        <div className="totalPrice">{`TOTAL PRICE: $${price*quantities}`}</div>
      </div>
      <div className="buyBtns">
        <button className="buy">BUY</button>
        <button className="toCart">ADD TO CART</button>
      </div>
    </div>
  </div>
  
  );
}
