import { useParams } from "react-router-dom";
import RegisterForm from "./Register";
import LoginForm from "./Login";
import { auth } from "../firebase/firebase";
import { useEffect,useState} from 'react';
import Signout from "./Signout";



const Signing = () => {
  const type = useParams().type==='up'?true:false;
  var isLogged=useState(auth.currentUser?true:false);
  useEffect(()=>{
    
    isLogged=auth.currentUser?true:false;
    
    
  },[auth.currentUser])

  console.log(isLogged);
  return <div className="signingComp">{!isLogged?(type?<RegisterForm/>:<LoginForm/>):<Signout/>}</div>;
}
 
export default Signing;
