import { auth } from "./firebase";
import { createUserWithEmailAndPassword,signInWithEmailAndPassword,signInWithPopup,signInWithRedirect,GoogleAuthProvider, getRedirectResult } from "firebase/auth";



const addUser = (token)=>{
  const options = {    method: "POST", 
  body:JSON.stringify({
    token:token,
    cart:[]
  }), 
  headers: {
    'Access-Control-Allow-Origin':"http://localhost:3000",
    "Content-Type": "application/json"
  }};
  console.log('added')
  fetch('http://localhost:8080/user',options).then((res)=>{
    return res.json();

  }).then(res=>{
    console.log(res);
    
  }).catch((err)=>{console.log(JSON.parse(err));});

}
const setUserData = (token)=>{
  const options = {    method: "GET", 
  headers: {
    'Access-Control-Allow-Origin':"http://localhost:3000",
    "Content-Type": "application/json"
  }};

  fetch(`http://localhost:8080/user/${token}`,options).then((res)=>{
    return res.json();

  }).then(res=>{
    localStorage.setItem("userData",res);
    console.log(res);
    
  }).catch((err)=>{
    console.log(err);
  });

}

auth.onAuthStateChanged((user)=>{
  if(!user)localStorage.setItem("userData",null);
  else {
    setUserData(user.accessToken);
  }
  
  
})

export const doCreateUserWithEmailAndPassword=async(email,password)=>{
  
