import { useEffect } from 'react';
import './App.css';
import Home from './Home';
import Menu from './Menu/Menu';
import ProductPage from './ProductPage/ProductPage';
import { Route,Routes, useLocation} from 'react-router-dom';

function App() {

  var scrolled = false;
  const {pathname} = useLocation();

  useEffect(()=>{
    console.log(pathname);
    if(pathname==='/'){
      localStorage.setItem('scrolled',document.querySelector(".App").scrollTop)
      console.log(localStorage.getItem('scrolled'));
    }
  },scrolled)


  function loaded(){

    document.querySelector(".App").addEventListener('scroll',()=>{scrolled=true;});
  }
  return (<div className='App' onLoad={loaded}>
    <Menu/>
    <Routes>
      <Route path ='/' element={<Home/>}/>      
      <Route path ='product/:id' element={<ProductPage/>}/>      

    </Routes>
  </div>


  );
}

export default App;
