import {useState } from "react";

export function BuyUI(params){

  const {available,price,name} = params;
  const [quantities,setQuantities] = useState(1);
  const changeQuantity=(e, direction=0)=>{
    var iqua = parseInt(quantities);


    var quantToSet=e===undefined?(iqua+ direction<1?quantities:iqua+direction): e.target.value;
    
    if(quantToSet>available)quantToSet=quantities;
    setQuantities(quantToSet)
  }
  const preventInput = (evt)=>{
    var keys =["e","E","+","-",",","."]
    if(evt.target.value.length===0){
      keys.push('0');
    }
    keys.includes(evt.key) && evt.preventDefault()
  }
  const addToCart = ()=>{
    var currCart = localStorage.getItem("cart");
  }

  return(
  <div className="BuyUI">
    <div className="container">
      <label className="name">{name}</label>
      <div className="available">AVAILABLE: {available}</div>
      <div className="quantity">
        <button className="minus" onClick={()=>{changeQuantity(undefined,-1)}}> - </button>
        <input onKeyDown={(evt)=>preventInput(evt)} value={quantities} onChange={(e)=>{changeQuantity(e)}} type="number" min={1}/>
        <button className="plus" onClick={()=>{changeQuantity(undefined,1)}}> + </button>
      </div>
      <div className="price">
        <div className="perU">PER UNIT: ${price}</div>
        <div className="summary">{`SUMMARY: ${quantities} x $${price}`}</div>
        <div className="totalPrice">{`TOTAL PRICE: $${(price*quantities).toFixed(2)}`}</div>
      </div>
      <div className="buyBtns">
        <div className="buy">BUY</div>
        <div className="toCart" onClick={addToCart}>ADD TO CART</div>
      </div>
    </div>
  </div>
  
  );
}
