  var quantities = 1;

  return(
  <div className="BuyUI">
    <div>{name}</div>
    <div className="quantity"></div>
      <button className="minus"> - </button>
      <input onChange={(e)=>{quantities=e.target.value; console.log(quantities)}}defaultValue={1} type="number" min={1}/>
      <button className="plus"> + </button>
  </div>
  );
}
