import { useParams } from "react-router-dom";
import RegisterForm from "./Register";
import LoginForm from "./Login";
import { auth } from "../firebase/firebase";
import {useState} from 'react';



const Signing = () => {
  const type = useParams().type==='up'?true:false;
  const [isLogged,setIsLogged]= useState(auth.currentUser?true:false);
  auth.onAuthStateChanged(user=>{
    console.log(user) 
    setIsLogged(user?true:false);
  })

  console.log(isLogged);
  return <div className="signingComp">{!isLogged?(type?<RegisterForm />:<LoginForm />):<div>You are already logged</div>}</div>;
}
 
export default Signing;
