import { useState } from 'react';

export function ImgSlider(params) {
  const {imgs} = params;
  const firstImg = imgs[0];
  const lastImg = imgs[imgs.length-1];
  const [active,setActive]=useState(0);
  function changeImg(direction){
    console.log(active);
    if(direction===-1){
      setActive(active+1);
    }else if(direction===1)
    {
      setActive(active-1);
    }
    console.log(active);
  }
  return (
      <div className='slider'>
        {
          imgs.map((el,id)=>{
            return <img className={active-id<0?'p'+Math.abs(active-id):'o'+(active-id)} src={el}/>
          })
        }
      </div>
  );
}
