import { useState } from "react";

export function BuyUI(price,available,name){

  const [quantities,setQuantities] = useState(1);
  function changeQuantity(e, direction=0){
    var iqua = parseInt(quantities);
    var quantToSet=e===undefined?(iqua+ direction<1?quantities:iqua+direction): e.target.value;
    
    if(quantToSet>available)quantToSet=quantities;
    setQuantities(quantToSet)
    console.log(quantities);
  }

  


  return(
  <div className="BuyUI">
    <div>{name}</div>
    <div className="quantity"></div>
      <button className="minus" onClick={()=>{changeQuantity(undefined,-1)}}> - </button>
      <input value={quantities} onChange={(e)=>{changeQuantity(e)}} type="number" min={1}/>
      <button className="plus" onClick={()=>{changeQuantity(undefined,1)}}> + </button>
  </div>
  );
}
