import { auth } from "./firebase";
import { createUserWithEmailAndPassword,signInWithEmailAndPassword,signInWithPopup,signInWithRedirect,GoogleAuthProvider, getRedirectResult } from "firebase/auth";


const DetectDevice = ()=>{
  let isMobile = window.matchMedia;
  if (isMobile) {
    let match_mobile = isMobile("(pointer:coarse)");
    return match_mobile.matches;
  }
  localStorage.setItem("isMobile",isMobile);
  return false;
}
const addUser = (token)=>{
  console.log(token);
  const options = {    method: "POST", 
  body:JSON.stringify({
    token:token,
    cart:[]
  }),
  headers: {
    'Access-Control-Allow-Origin':"http://localhost:3000",
    "Content-Type": "application/json"
  }};
  console.log('added')
  fetch('http://localhost:8080/user',options).then((res)=>{
    return res.json();

  }).then(res=>{
    console.log(res);
    
  }).catch((err)=>{console.log(err);});

}


export const doCreateUserWithEmailAndPassword=async(email,password)=>{
  
  createUserWithEmailAndPassword(auth,email,password)
  .then(user=>{

    addUser(user.user.getIdToken());
  })
  .catch(err=>{console.log(err.code)});//TODO:make error handling
}
export const doSignInWithEmailAndPassword=async(email,password)=>{
  signInWithEmailAndPassword(auth,email,password)
  .then(()=>{
    window.location.pathname = '';   
  });
}

export const doSignInWithGoogle = async()=>{
  const provider = new GoogleAuthProvider();
  if(!DetectDevice()){
    signInWithPopup(auth,provider)
    .then(result=>{
      const credentials = GoogleAuthProvider.credentialFromResult(result);
      console.log(credentials);
      const token = credentials?.accessToken;
      console.log(token);
    }).catch(error=>{
      const errorCode = error.code;
      const errorMessage = error.message;
      const email = error.customData?.email;
      const credentials = GoogleAuthProvider.credentialFromError(error)
    })
  }else{
    signInWithRedirect(auth,provider);
    getRedirectResult(auth)
    .then(result=>{
      if(result){
        const credentials = GoogleAuthProvider.credentialFromResult(result);
        console.log(credentials);
        const token = credentials?.accessToken;
        const user = result.user;
      }
    }).catch(error=>{
      const errorCode = error.code;
      const errorMessage = error.message;
      const email = error.customData.email;
      const credentials = GoogleAuthProvider.credentialFromError(error);
    })
  }
}

export const doSignOut=()=>{
  return auth.signOut();
}
